import React, { useState, useEffect } from 'react';

const Task = ({ task, onComplete, onDrop }) => {
  const [clicked, setClicked] = useState(false);

  const handleComplete = () => {
    onComplete(task.id);
    setClicked(false);
  };

  const handleDrop = () => {
    onDrop(task.id);
    setClicked(false);
  };

  return (
    <div
      className={`flex items-center justify-between p-4 border-b border-gray-300 ${
        clicked ? 'bg-gray-100' : ''
      }`}
      onClick={() => setClicked(true)}
    >
      <span>{task.title}</span>
      {clicked && (
        <div className="flex space-x-4">
          <button
            className="px-2 py-1 bg-green-500 text-white rounded hover:bg-green-600"
            onClick={handleComplete}
          >
            Complete
          </button>
          <button
            className="px-2 py-1 bg-red-500 text-white rounded hover:bg-red-600"
            onClick={handleDrop}
          >
            Drop
          </button>
        </div>
      )}
    </div>
  );
};

const TaskList = ({ tasks, onComplete, onDrop }) => {
  return (
    <div className="mt-4">
      {tasks.map((task) => (
        <Task
          key={task.id}
          task={task}
          onComplete={onComplete}
          onDrop={onDrop}
        />
      ))}
    </div>
  );
};

const AddTaskForm = ({ onAddTask }) => {
  const [title, setTitle] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;
    try {
      const response = await fetch('http://localhost:3000/addtask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title }),
      });
      if (response.ok) {
        onAddTask(await response.json());
        setTitle('');
      } else {
        console.error('Failed to add task:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to add task:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Add a new task..."
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="border border-gray-300 rounded p-2 mr-2"
      />
      <button
        type="submit"
        className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Add Task
      </button>
    </form>
  );
};

const TodoApp = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:3000/gettasklist');
        if (response.ok) {
          const data = await response.json();
          setTasks(data);
        } else {
          console.error('Failed to fetch tasks:', response.statusText);
        }
      } catch (error) {
        console.error('Failed to fetch tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  const addTask = (newTask) => {
    setTasks([...tasks, newTask]);
  };

  const completeTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3000/edittask/${taskId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ completed: true }),
      });
      if (response.ok) {
        setTasks(tasks.map((task) => (task.id === taskId ? { ...task, completed: true } : task)));
      } else {
        console.error('Failed to complete task:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to complete task:', error);
    }
  };

  const dropTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3000/droptask/${taskId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setTasks(tasks.filter((task) => task.id !== taskId));
      } else {
        console.error('Failed to drop task:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to drop task:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Todo List</h1>
      <AddTaskForm onAddTask={addTask} />
      <TaskList tasks={tasks} onComplete={completeTask} onDrop={dropTask} />
    </div>
  );
};

export default TodoApp;
