import React, { useState, useEffect } from 'react';
import TaskList from '../Components/TaskList';
import AddTaskForm from '../Components/AddTaskForm';


const TodoApp = () => {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/gettasklist');
      if (response.ok) {
        const data = await response.json();
        console.log(data[0]);
        setTasks(data);
      } else {
        console.error('Failed to fetch tasks:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to fetch tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = () => {
    fetchTasks();
  };

  const completeTask = async (id) => {
    try {
      const response = await fetch(`/api/edittask/${id}`, {
        method: 'PATCH', // Change method to PATCH
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ completed: true }) // Send the updated data
      });
      if (response.ok) {
        const updatedTask = await response.json();
        setTasks(tasks.map(task => (task.id === id ? updatedTask : task)));
      } else {
        console.error('Failed to complete task:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to complete task:', error);
    }
  };
  const dropTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:3000/api/droptask/${taskId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setTasks(tasks.filter((task) => task.id !== taskId));
      } else {
        console.error('Failed to drop task:', response.statusText);
      }
    } catch (error) {
      console.error('Failed to drop task:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Todo List</h1>
      <AddTaskForm onAddTask={addTask} />
      <TaskList tasks={tasks} onComplete={completeTask} onDrop={dropTask} />
    </div>
  );
};

export default TodoApp;
