import React, { useState } from 'react';

const Task = ({ task, onComplete, onDrop }) => {
   
    const handleComplete = () => {
        console.log(task.id);
        onComplete(task.id);
    };

    const handleDrop = () => {
        onDrop(task.id);
    };

    return (
        <div
            className={`flex items-center justify-between p-4 border-b border-gray-600 bg-gray-100`}
        >
            <span style={task.completed ?{textDecoration:"line-through"}:{}}>{task.title}</span>
            
                <div className="flex space-x-4">
                    {!task.completed && <button
                        className="px-2 py-1 bg-green-500 text-white rounded hover:bg-green-600"
                        onClick={handleComplete}
                    >
                        Complete
                    </button>}
                    <button
                        className="px-2 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                        onClick={handleDrop}
                    >
                        Drop
                    </button>
                </div>

        </div>
    );
};



const TaskList = ({ tasks, onComplete, onDrop }) => {
    return (
        <div className="mt-4">
            {tasks.map((task,index) => (
                <Task
                    key={index}
                    task={task}
                    onComplete={onComplete}
                    onDrop={onDrop}
                />
            ))}
        </div>
    );
};
export default TaskList;
